// src/WaveButtonCanvas.js
import React from 'react';
import { Stage, Layer, Rect, Circle, Text, Group, Shape } from 'react-konva';

const OLDiagram = () => {
    const waveButtonDims = {
        "Principles": { Width: 170, Height: 110, CornerRadius: 40, Color: "#99f6be" },
        "Dimensions": { Width: 170, Height: 110, CornerRadius: 40, Color: "99f6be" },
        "Perspectives": { Width: 170, Height: 110, CornerRadius: 40, Color: "99f6be" }
    };

    function drawWaveButton(x, y, context, shape, width, height, cornerRadius) {
        const halfWidth = width / 2;
        const halfHeight = height / 2;

        const top = { x: x, y: y - halfHeight };
        const right = { x: x + halfWidth, y: y };
        const bottom = { x: x, y: y + halfHeight };
        const left = { x: x - halfWidth, y: y };

        context.beginPath();
        context.moveTo(right.x, right.y);
        context.arcTo(bottom.x, bottom.y, left.x, left.y, cornerRadius);
        context.lineTo(left.x, left.y);
        context.arcTo(top.x, top.y, right.x, right.y, cornerRadius);
        context.closePath();
        context.fillStrokeShape(shape); 

        return [context, shape];
    }

    const handleClick = (e) => {
        const id = e.target.id();
        alert(principles[id]['infoText'])
      }

    function getPrinciples() {
        const x = 200;
        const y = 200;

        const width = waveButtonDims.Principles['Width'];
        const height = waveButtonDims.Principles['Height'];
        const halfWidth = width / 2;
        const halfHeight = height / 2;
        const margin = 3;

        const p1 = { x: x, y: y };
        const p2 = { x: x - halfWidth - margin, y: y - halfHeight - margin };
        const p3 = { x: x + halfWidth + margin, y: y - halfHeight - margin };
        const p4 = { x: x, y: y - height - 2 * margin };
        const p5 = { x: x - halfWidth - margin, y: y + halfHeight + margin };
        const p6 = { x: x + halfWidth + margin, y: y + halfHeight + margin };
        const p7 = { x: x, y: y + height + 2 * margin };

        const arr = [0, 1, 2, 3, 4, 5, 6]; //MUDARRRRR
        const refPoints = [p1, p2, p3, p4, p5, p6, p7];
        const mainTexts = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];
        const idTexts = ['P1', 'P2', 'P3', 'P4', 'P5', 'P6', 'P7'];
        const infoTexts = ['a', 'b', 'c', 'd', 'e', 'f', 'g'];

        const principle = {
            "P1": { XRefPoint: p1['x'], YRefPoint: p1['y'], MainText: 'A', IdText: 'P1', infoText: 'a' },
            "P2": { XRefPoint: p1['x'], YRefPoint: p1['y'], MainText: 'A', IdText: 'P1', infoText: 'a' },

        }

        // return refPoints.map((i) => ({
        //     id: i.toString(),
        //     x: refPoints[i].x,
        //     y: refPoints[i].y,
        //     mainText: mainTexts[i],
        //     idText: idTexts[i],
        //     infoText: infoTexts[i],
        // }));

        return principle;
    }


    var arr = [{"name":"aName","lastName":"aLastname"},{"name":"bName","lastName":"bLastname"}];

    arr.forEach(function(d){
        console.log(d.name, d.lastName);
    });


    let principles = getPrinciples();
    console.log(getPrinciples())
    return (
        <Stage width={window.innerWidth} height={window.innerHeight}>
            {principles.forEach((p) => (
            <Layer>
                <Shape
                sceneFunc={(context, shape) => {
                drawWaveButton(p.XRefPoint, p.YRefPoint, context, shape, waveButtonDims.Principles['Width'], waveButtonDims.Principles['Height'], waveButtonDims.Principles['CornerRadius'])
                }}
                key={p['id']}
                id={p['id']}
                fill={waveButtonDims.Principles['Color']}
                onClick={handleClick}
                />
                <Text fontSize={20} text={p.MainText} align="center" x={p.XRefPoint} y={p.YRefPoint}/>
            </Layer>
            ))}   
    </Stage>
    );
};

export default OLDiagram;
